connection = ActiveRecord::Base.connection();
connection.execute("





CREATE TYPE group_type AS ENUM (
    'unique',
    'non-unique',
    'specific'
);
ALTER TYPE public.group_type OWNER TO postgres;



CREATE TABLE factions (
    id  SERIAL  NOT NULL PRIMARY KEY ,
    title text,
    link text
);


CREATE TABLE categories (
    title text,
    link text,
    faction_id integer,
  CONSTRAINT categories_link_faction_id PRIMARY KEY(link, faction_id)
);


CREATE TABLE  products (
  id   SERIAL NOT NULL PRIMARY KEY,
  category_link TEXT NOT NULL,
  title text NOT NULL,
  price numeric(10,0) NOT NULL,
  availability integer NOT NULL,
  link TEXT NOT NULL,
  photo TEXT NOT NULL,
  photos text NOT NULL,
  short_description text NOT NULL,
  description text NOT NULL
);
CREATE INDEX price ON products (price);
CREATE INDEX availability ON products (availability);
CREATE INDEX price_availability ON products (price, availability);


CREATE TABLE  groups (
  category_link TEXT NOT NULL,
  link TEXT NOT NULL,
  type group_type,
  title text NOT NULL,
  CONSTRAINT category_link_link PRIMARY KEY(category_link, link)
);
CREATE INDEX category_link ON groups (category_link);


CREATE TABLE  group_values (
  category_link TEXT NOT NULL,
  group_link TEXT NOT NULL,
  id integer NOT NULL,
  title text NOT NULL,
  products text NOT NULL,
  CONSTRAINT category_link__group_link_id PRIMARY KEY(category_link, group_link, id)
);
CREATE INDEX category_link_group_link ON group_values (category_link,group_link);


CREATE TABLE group_products (
  product_id integer NOT NULL,
  code TEXT NOT NULL,
  value decimal(4,2) NOT NULL,
  CONSTRAINT group_products PRIMARY KEY(product_id, code)
);




CREATE TABLE  users (
  id SERIAL NOT NULL PRIMARY KEY,
  title TEXT NOT NULL
);

CREATE TABLE  comments (
  id SERIAL NOT NULL PRIMARY KEY,
  title TEXT NOT NULL,
  body TEXT NOT NULL,
  user_id integer NOT NULL,
  product_id integer NOT NULL
);
CREATE INDEX comments_iser_id__product_id ON comments (user_id, product_id);











